package com.esaulpaugh.headlong.rlp.example;

import com.esaulpaugh.headlong.util.Strings;
import org.junit.jupiter.api.Test;

import java.nio.ByteBuffer;

import static com.esaulpaugh.headlong.util.Strings.HEX;
import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class RLPStudentTest {

    public static final String STUDENT_RLP_SEQUENTIAL = "";

    public static final String STUDENT_RLP_LIST = "";

    public static final String STUDENT_TO_STRING = "Plato, 9000.01, , $2552.7185792349726775956284153005464480874316939890710382513661202185792349726775956284153005464480874316939890710382513661202";

    @Test
    public void rlpDecodeEncode() {
        final byte[] studentRlp = Strings.decode(STUDENT_RLP_SEQUENTIAL, HEX);
        RLPStudent plato = new RLPStudent(studentRlp);
        assertEquals(STUDENT_TO_STRING, plato.toString());

        byte[] rlp = new byte[205];
        plato.toRLP(ByteBuffer.wrap(rlp));
        assertArrayEquals(studentRlp, rlp);
    }

    @Test
    public void adapterDecodeEncode() {

        RLPStudentAdapter adapter = new RLPStudentAdapter();

        final byte[] studentRlp = Strings.decode(STUDENT_RLP_LIST, HEX);
        RLPStudent plato = adapter.decode(studentRlp);

        assertEquals(STUDENT_TO_STRING, plato.toString());

        byte[] rlp = adapter.encode(plato);

        assertArrayEquals(studentRlp, rlp);
    }
}
